import { configureStore } from "@reduxjs/toolkit";
import uiSliceReducer from "./ui-slice.js";
import cartSlice from "./cart-slice.js";

const store = configureStore({
  reducer: {
    ui: uiSliceReducer,
  },
});

export default store;
